var count = 0;

function cc(card) {
  // Only change code below this line
  var decision = "";

  if (card === 2 || card === 3 || card === 4 || card === 5 || card === 6){
    ++count;
    }

  else if (card === 10 || card === "J" || card === "K" || card === "Q" || card === "A"){
    --count;
  }

  if(count>0){
      decision = " Bet";
  }

  else{
      decision = " Hold";
  }

  return count + decision;
  // Only change code above this line
}

